mapper.select:
select count(*)
from animal
where member_no = #{mId}

select count(*)
from jibsa
where member_no = #{mId}

mapper.update:
update member set
member_name = #{memberName}, is_jibsa = #{isJibsa}
member_name = #{memberName}, member_email = #{memberEmail}, is_admin = #{isAdmin}, is_jibsa = #{isJibsa}, member_status = #{memberStatus}, member_phone = #{memberPhone}, member_address = #{memberAddress}
where member_no = #{memberNo}

update matching
set review_status = #{reviewStatus} 
where matching_no = #{matchingNo}

update matching
set start_date = #{startDate},
	end_date = #{endDate},
	matching_Place = #{matchingPlace},
	matching_status = #{matchingStatus}
where matching_no = #{matchingNo}

update faq
set faq_title = #{faqTitle},
	faq_content = #{faqContent},
	faq_status = #{faqStatus}
where faq_no = #{faqNo}

update qna
set qna_answer = #{qnaAnswer},
	answer_date = sysdate,
	qna_status = #{qnaStatus}
where qna_no = #{qnaNo}
